function App() {
  const ctx = useContext(AuthContext);
  return (
    <React.Fragment>
      <MainHeader onLogout={logoutHandler} />
      <main>
        {!ctx.isLoggedIn && <Login />}
        {ctx.isLoggedIn && <Home />}
